app.post('/resizes', function(req, res) {
  // validate the uploaded image file
  fs.readFile(req.files[0].path, function(error, data) {
    if (!error && data.length > 0) {
      // extract thumbnail from original image
      var width = req.body.width;
      var height = req.body.height;

      // generate thumbnail based on extracted dimensions
      var thumbnailData = imagick.createThumbnail({
        src: data,
        width: width,
        height: height
      }, '100x100');

