module fsm_example(input [7:0] input_data,
                 output logic[3:0] out_count);
  // Define the state variables
  localparam State_IDLE = 0;
  localparam State_COUNTING = 1;

  // Configure the transition table
  transition_table my_table(
    State_IDLE => State_COUNTING,
    State_COUNTING => State_IDLE
  );

  // Set up the logic for the counting state
  always @(posedge clock) begin
    if (rising_edge(clock)) begin
      out_count <= 7'b11111110; // set bit counter to 9 bits
      state <= State_COUNTING;
    end
  end

  // Set up the logic for the idle state
  always @(posedge clock) begin
    if (rising_edge(clock)) begin
      state <= State_IDLE;
    end
  end

  // Implement the end-of-stream condition
  reg end_of_stream;
  initial begin
    end_of_stream = 1'd1;
    while (!end_of_stream) begin
      input_data >>= 1;
      state <= next_state();
    end
  end
end module

