    lineHeight: 1.5,
    minHeight: "50%",
  },
});

class Editor extends React.Component<any> {
  constructor(props) {
    super(props);

    this.state = {
      writingSpeed: 0,
      isEditing: false,
    };
  }

  componentDidMount() {
    window.addEventListener("resize", this.onWindowResize);
  }

  onWindowResize = () => {
    // Update the viewport size for proper rendering
    window.requestAnimationFrame(() => this.onWindowResize());
  };

  onKeyDown = (event) => {
    if (!this.state.isEditing && event.keyCode === 13 /* Enter */) {
      this.setState({ isEditing: true });
    }
  };

  onSubmit = async (value) => {
    try {
      const newWritingSpeed = parseInt(value);
      await this.setState({ writingSpeed: newWritingSpeed });
    } catch (e) {}
  };

  render() {
    return (
      <div style={styles.root}>
        <TextInput
          type="text"
          label="Your Writing Speed (words per minute)"
          variant="outlined"
          style={{ width: "100%" }}
          ref={input => (this.input = input)}
          onChange={event => this.onValueChanged(event.target.value as number)}
        />

        <br />

        {/* The submit button goes here */}
        <Button variant="contained" color="primary">Add Word</Button>

        {/* If the input is empty or has no changes since last editing... */}
        {!!this.input &&!!this.state.isEditing? (
          <>
            <Typography component="span" style={{ fontWeight: 900 }}>
              You wrote {this.state.writingSpeed} words in {this.state.timeSpentInEditor / 1000} minutes.
            </Typography>
            <br />
            <button type="reset" value="Reset" onClick={this.onReset}> Reset </button>
          </>
        ): null}
      </div>
    );
  }
}

export default Editor;
