#include <Flex.h> 

// Define the pin that the flex sensor is connected to on the Arduino board
const int sensorsPin = A0; // A0 is the default for the analog 0 pin


void setup() {
  Serial.begin(9600);
}

void loop() {

  // Read the value from the flex sensor and store it as a float
  float sensorValue = readSensor();

  if (sensorValue > 100) {
    digitalWrite(LED, HIGH); // Turn the LED on if the sensor reading is greater than 100
  } else if (sensorValue <= 50) {
    digitalWrite(LED, LOW); // Turn the LED off if the sensor reading is less than 50
  }

  delay(500);
}

// Function to read the value from the flex sensor
float readSensor() {
  // Initialize the Flex object with the specified pin number
  Flex flex(sensorsPin);

  // Wait until the flex sensor has been detected
  while (!flex.isDetected()) {}

  // Get the sensor readings from the flex sensor
  int sensorReading = flex.getReadings()[0];

  return sensorReading * 10;
}
