def _multipart_encode(boundary, fields):
    """
        Helper function for multipart encoding. Returns an encoded string
    """
    lines = []
    for key, value in fields:
        if not (isinstance(value, str) or isinstance(value, unicode)):
            raise TypeError("Non-string type field found")
        line = "--" + boundary + "\r\n" \
                "{field}={val}" \
                ".{linebreak}\r\n".format(**{"field": key, "val": value})
        lines.append(line)
    return b'\r\n'.join(lines)

